function escapeHtml(str){return String(str).replace(/[&<>"']/g,s=>({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[s]));}
function applyBoldMarkers(escaped){return escaped.replace(/##(.*?)##/g,'<strong>$1</strong>');}
function format(val){
  if(val==null) return '';
  if(typeof val==='string'){
    let out=escapeHtml(val);
    out=out.replace(/(\n|^)\s*-\s+/g,'<br/>• ');
    out=out.replace(/##\s*(.+?)\s*##/g,'<br/><br/><strong>$1</strong>');
    return out;
  }
  if(Array.isArray(val)) return `<ul>${val.map(v=>`<li>${format(v)}</li>`).join('')}</ul>`;
  if(typeof val==='object') return `<ul>${Object.entries(val).map(([k,v])=>`<li><strong>${escapeHtml(String(k))}:</strong> ${format(v)}</li>`).join('')}</ul>`;
  return applyBoldMarkers(escapeHtml(String(val)));
}
function pick(pathFn, fallback=''){try{const v=pathFn();return v==null?fallback:v;}catch{return fallback;}}
function slugify(s){return String(s||'').trim().toLowerCase().replace(/[^a-z0-9]+/g,'-').replace(/(^-|-$)/g,'');}
// Simple random 20-char string (letters + numbers)
function rand20(){
  const chars='abcdefghijklmnopqrstuvwxyz0123456789';
  let res='';
  for(let i=0;i<20;i++) res+=chars[Math.floor(Math.random()*chars.length)];
  return res;
}

