// --- helpers (Rich Text safe) ---

function escapeHtml(str){return String(str).replace(/[&<>"']/g,s=>({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[s]));}

// Convert ##bold## → <strong>, - bullets → <ul><li>…</li></ul>, paragraphs from line breaks.
// Returns HTML that a Webflow Rich Text field will render.
function toRichText(val){
  if (val == null) return '';
  if (typeof val !== 'string') val = JSON.stringify(val, null, 2);

  // escape first so user content is safe
  let out = escapeHtml(val);

  // ##bold## markers → <strong>
out = out.replace(/##\s*(.+?)\s*##/g, '<strong>$1</strong>');
out = out.replace(/\*\*\s*(.+?)\s*\*\*/g, '<strong>$1</strong>');

  // Split into lines to build lists/paragraphs
  const lines = out.split(/\r?\n/);
  const blocks = [];
  let listBuffer = [];

  function flushList(){
    if (listBuffer.length){
      blocks.push('<ul>' + listBuffer.map(li => `<li>${li}</li>`).join('') + '</ul>');
      listBuffer = [];
    }
  }

  for (let line of lines){
    const trimmed = line.trim();

    if (!trimmed){ // blank => paragraph break
      flushList();
      blocks.push(''); // mark break; we'll collapse later
      continue;
    }

    // bullet line: "- something"
    const m = trimmed.match(/^-+\s+(.*)$/);
    if (m){
      listBuffer.push(m[1]);
      continue;
    }

    // normal text → paragraph
    flushList();
    blocks.push(`<p>${trimmed}</p>`);
  }
  flushList();

  // collapse extra blanks between blocks
  return blocks.filter(Boolean).join('');
}

