// Function node (not Function Item)
const d = $input.first().json.output;
const pitch = d.message.elevatorPitch;

// Build HTML
const html = `
<!doctype html>
<html>
<head>
  <meta charset="utf-8">
  <title>Elevator Pitch</title>
</head>
<body>
  <h1>Elevator Pitch</h1>
  <p>${pitch}</p>
</body>
</html>
`;

// Encode as binary
const b64 = Buffer.from(html, 'utf8').toString('base64');

return [{
  json: $json, // keep original JSON if needed downstream
  binary: {
    html: {
      data: b64,
      fileName: 'index.html',
      mimeType: 'text/html',
    },
  },
}];

// === CONFIG: set these per agent ===
const NODE_NAME  = "Media Node";      // exact agent node name
const PLACEHOLDER = "MEDIA";          // {{MEDIA}} in the Doc
const OUTPUT_KEY  = "media";          // key under .output if not using .items

// === Get docId from Copy File node ===
const docId = $('Copy file').first().json.id;
if (!docId) throw new Error("Missing docId. Ensure Copy file node runs before this.");

// === Read agent text robustly (supports .items or .output.<key>) ===
const src = $node[NODE_NAME].json || {};
let text = "";
if (typeof src.items === "string") text = src.items;
else if (src.output && typeof src.output[OUTPUT_KEY] === "string") text = src.output[OUTPUT_KEY];
else {
  const out = src.output || {};
  for (const k of Object.keys(out)) if (typeof out[k] === "string") { text = out[k]; break; }
}

// === üî§ Format markdown-ish syntax for Google Docs readability ===
text = text
  // Remove "##" and make headings uppercase
  .replace(/^##\s*(.*)$/gm, (_, m) => m.toUpperCase())
  // Replace "-" at line starts with real bullets
  .replace(/^- /gm, "‚Ä¢ ");

// === Build replaceAllText request ===
const requests = [{
  replaceAllText: {
    containsText: { text: `{{${PLACEHOLDER}}}`, matchCase: true },
    replaceText: String(text || "")
  }
}];

return [{ json: { docId, requests } }];

