  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "company": {
      "type": "string",
      "description": "Company name"
    },
    "offer": {
      "type": "string",
      "description": "Description of the company's offering"
    },
    "primary_target_market": {
      "type": "object",
      "properties": {
        "audience_type": {
          "type": "string",
          "description": "Specify whether this target market is B2B, B2C, or both"
        },
