            "profession": {
              "type": "string",
              "description": "Professional background or role"
            },
            "business_size": {
              "type": "string",
              "description": "Size of the business (if applicable)"
            }
          }
        },
        "psychographics": {
          "type": "object",
          "properties": {
            "lifestyle": {
              "type": "string",
              "description": "Lifestyle characteristics of the target market"
            },
            "values": {
              "type": "string",
              "description": "Core values or beliefs"
            },
            "pain_points": {
              "type": "string",
              "description": "Primary challenges or pain points"
            },
            "buying_behavior": {
              "type": "string",
              "description": "Buying habits and decision factors"
            }
          }
        },
        "decision_making_roles": {
          "type": "object",
          "properties": {
