    "AgendaScore": {
      "type": "string",
      "enum": ["1", "3", "5", "7"],
      "description": "String representing agenda-setting score out of 7"
    },
    "EnergyScore": {
      "type": "string",
      "enum": ["1", "3", "5", "7"],
      "description": "String representing energy score out of 7"
