    "EnergyScore",
    "SummaryActionScore"
  ]
}

parameters.jsCode:
const data = $input.first().json.output;

return [
  {
    json: {
      ...data,
      RapportScore: parseInt(data.RapportScore, 10),
      AgendaScore: parseInt(data.AgendaScore, 10),
      EnergyScore: parseInt(data.EnergyScore, 10),
      SummaryActionScore: parseInt(data.SummaryActionScore, 10)
    }
  }
];

const merged = $input.all();

let companyArr = null;
let emailsObj = null;

for (const item of merged) {
  if (Array.isArray(item.json.data)) {
    companyArr = item.json.data;
  }
  if (item.json.output && item.json.output['company Emails']) {
    emailsObj = item.json.output;
  }
}

if (!companyArr || !emailsObj) {
  throw new Error("Could not find expected data structure.");
}

const emails = emailsObj['company Emails']
  .split(',')
  .map(e => e.trim().toLowerCase());

const emailToCompany = {};
for (const obj of companyArr) {
  emailToCompany[obj.Email.toLowerCase()] = obj["Company as in Notion"];
}

// Main match results
const matchResults = emails.map(email => ({
  type: 'email_match', // distinguish this output
  Email: email,
  "Company as in Notion": emailToCompany[email] || null,
}));

// --- Custom additional item from another node ---
// Example: Let's just mock this for now, you can replace this with actual mapping logic
const otherNodeItem = {
  type: 'external_data',   // distinguish this output
  someField: 'exampleValue',
  anotherField: $('Extract from File').first().json.data,
};

// Return both as separate outputs
return [
  ...matchResults.map(j => ({ json: j })),
  { json: otherNodeItem }
];
