// Get the URL from the 'Transcript ID' field of the first input item
const url = $input.first().json['Google Doc Transcript'];

if (!url || typeof url !== 'string') {
  throw new Error("Missing or invalid 'Google Doc Transcript' (URL) in the input.");
}

// Extract the document ID from the Google Docs URL
const match = url.match(/\/d\/([^/]+)/);

if (!match || !match[1]) {
  throw new Error("No valid Google Doc ID found in the 'Google Doc Transcript' field.");
}

// Return the result with the desired key name
return {
  json: {
    'Google Doc Transcript': match[1],
  },
};

