{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Coaching Call Analysis Output",
  "description": "Schema for structured call analysis agent output from coaching call transcripts",
  "type": "object",
  "properties": {
    "company_name": {
      "type": "string",
      "description": "The name of the company"
    },
    "host": {
      "type": "string",
      "description": "Name of the coach or advisor who hosted the call"
    },
    "summary": {
      "type": "string",
      "description": "Concise title and 2–4 short sentences summarizing key discussion points, primary goals, obstacles, and client-coach alignment"
    },
    "discussion_points": {
      "type": "string",
      "description": "Structured bullet list of concrete follow-up actions with responsible party and deadlines"
    },
    "date_of_call": {
      "type": "string",
      "pattern": "^[A-Za-z]+ \\d{1,2}, \\d{4}$",
      "description": "Date of the call in 'Month Day, YYYY' format (e.g., 'January 15, 2025')"
    },
    "consulting_health": {
      "type": "string",
      "enum": ["Green", "Yellow", "Red"],
      "description": "Client implementation status: Green (strong progress), Yellow (partial/stagnation), Red (no implementation/falling behind)"
    },
    "sentiment": {
      "type": "string",
      "enum": ["Positive", "Uncertain", "Unrealistic expectations", "Negative"],
      "description": "Client buy-in and engagement classification"
    },
    "link_to_call_recording": {
      "type": "string",
      "format": "uri",
      "pattern": "^https://fathom\\.video",
      "description": "Direct URL to the call recording from the document (must start with https://fathom.video)"
    },
    "archetype": {
      "type": "string",
      "enum": ["Doer", "Driver", "Worrier", "Follower"],
      "description": "Client classification: Doer (high self-confidence, low tool confidence), Driver (high both, distrusts advisor), Worrier (low both, overthinks), Follower (low self, high tool confidence)"
    },
    "red_flags": {
      "type": "string",
      "description": "Three bullet points of major client blockers with timestamps and negative emotions"
    },
    "green_lights": {
      "type": "string",
      "description": "Three bullet points of positive signals with timestamps and positive emotions"
    },
    "rapport_score": {
      "type": "integer",
      "enum": [1, 3, 5, 7],
      "description": "Score for how well coach built rapport (warmth, curiosity, mirroring, pacing)"
    },
    "agenda_score": {
      "type": "integer",
      "enum": [1, 3, 5, 7],
      "description": "Score for agenda clarity and maintenance during the call"
    },
    "energy_score": {
      "type": "integer",
      "enum": [1, 3, 5, 7],
      "description": "Score for coach engagement, presence, and momentum maintenance"
    },
    "summary_action_score": {
      "type": "integer",
      "enum": [1, 3, 5, 7],
      "description": "Score for how well next steps were summarized and clarified"
    },
    "renewal_convo_analysis": {
      "type": ["string"],
      "description": "Two-paragraph analysis of contract renewal conversation (what worked well and what could improve), or write 'nothing discussed' if not applicable"
    },
    "action_items": {
      "type": "string",
      "description": "Bullet list of primary action items from the call"
    },
    "fathom_summary": {
      "type": "string",
      "description": "Structured Fathom-style summary with Title, Meeting Purpose, Key Takeaways, Topics, Next Steps, and Action Items"
    }
  },
  "required": [
    "company_name",
    "host",
    "summary",
    "discussion_points",
    "date_of_call",
    "consulting_health",
    "sentiment",
    "link_to_call_recording",
    "archetype",
    "red_flags",
    "green_lights",
    "rapport_score",
    "agenda_score",
    "energy_score",
    "summary_action_score",
    "action_items",
    "fathom_summary"
  ],
  "additionalProperties": false
}

