// 1. Grab your company data
const companyData = $input.first().json.data;
if (!Array.isArray(companyData)) {
  throw new Error('Missing or invalid companyData array');
}

// 2. Grab the full transcript text from input JSON (not from $node)
const fullText = $('Extract from File4').first().json.callTranscript
if (typeof fullText !== 'string') {
  throw new Error('Missing callTranscript string');
}

// 3. Isolate the line after "Attendees Emails"
const parts = fullText.split(/Attendees Emails\s*/i);
if (parts.length < 2) {
  throw new Error('Couldn’t find "Attendees Emails" header in transcript');
}
const afterEmails = parts[1];

// 4. Pull out the first non-empty line (that holds the comma-list)
const lines = afterEmails
  .split(/\r?\n/)
  .map(l => l.trim())
  .filter(l => l.length);
const emailLine = lines[0];
if (!emailLine.includes('@')) {
  throw new Error('No email line found after the header');
}

// 5. Parse into an array
const emails = emailLine
  .split(',')
  .map(e => e.trim().toLowerCase())
  .filter(Boolean);

// 6. Build your email → company lookup
const emailLookup = {};
companyData.forEach(row => {
  if (row.Email) {
    emailLookup[row.Email.toLowerCase()] = {
      companyName: row['Company as in Notion'],
      notionId:    row['Notion ID']
    };
  }
});

// 7. Match attendees
const matchResults = emails
  .map(email => {
    const m = emailLookup[email];
    if (!m) return null;
    return {
      json: {
        type:                'email_match',
        Email:               email,
        'Company as in Notion': m.companyName,
        'Notion ID':           m.notionId
      },
      pairedItem: { item: 0 }
    };
  })
  .filter(Boolean);

// 8. Package your external data (attach the full transcript here)
const external = {
  type:         'external_data',
  someField:    'exampleValue',
  anotherField: fullText
};

// 9. Merge & return
if (matchResults.length) {
  Object.assign(matchResults[0].json, external);
  return [ matchResults[0] ];
}
return [{ json: external, pairedItem: { item: 0 } }];
