// n8n Function node
// Input item shape: { state: { stack, storage, variables, ... }, ... }

function safeGet(obj, path, def = undefined) {
  return path.split('.').reduce((o,k)=> (o && k in o) ? o[k] : undefined, obj) ?? def;
}

const items = $input.all().map(({json}) => {
  const st = json.state || {};
  const vars = st.variables || {};
  const stack = st.stack || [];

  // 1) PRIMARY: variables._memory_  (array of {role, content})
  const mem = Array.isArray(vars._memory_) ? vars._memory_ : [];

  const memMsgs = mem
    .filter(m => m && typeof m.content === 'string' && typeof m.role === 'string')
    .map(m => ({
      role: m.role === 'function-call' || m.role === 'function-result' ? m.role : (m.role === 'user' ? 'user' : 'assistant'),
      content: m.role === 'function-call' || m.role === 'function-result'
        ? (m.role === 'function-call'
            ? `ðŸ”§ tool call: ${m.function?.name || 'unknown'} ${JSON.stringify(m.function?.arguments || {})}`
            : `ðŸ”§ tool result: ${typeof m.result === 'string' ? m.result : JSON.stringify(m.result)}`)
        : m.content
    }));

  // 2) FALLBACK: vf_memory (stitched string you can display if no mem)
  const vfMemoryStr = typeof vars.vf_memory === 'string' ? vars.vf_memory : '';

  // 3) ARTIFACT: stack[].storage.output  (array of Slate-like blocks -> join lines)
  const emailBlocks = [];
  for (const frame of stack) {
    const out = safeGet(frame, 'storage.output');
    if (Array.isArray(out)) {
      for (const block of out) {
        const text = Array.isArray(block.children)
          ? block.children.map(c => c?.text ?? '').join('')
          : '';
        emailBlocks.push(text);
      }
    }
  }
  // Clean collapsible empties and join
  const email_markdown = emailBlocks
    .map(s => (s || '').trim())
    .filter(s => s.length > 0)
    .join('\n\n');

  // If no _memory_, try to parse vf_memory into pseudo messages
  let messages = memMsgs;
  if (messages.length === 0 && vfMemoryStr) {
    // very light parser: split on newlines with "user:" / "assistant:" prefixes
    const lines = vfMemoryStr.split('\n');
    const tmp = [];
    let role = null, buf = [];
    const flush = () => {
      if (role && buf.length) tmp.push({ role, content: buf.join('\n').trim() });
      role = null; buf = [];
    };
    for (const line of lines) {
      const mUser = line.match(/^\s*user:\s*(.*)$/i);
      const mAsst = line.match(/^\s*assistant:\s*(.*)$/i);
      const mToolC = line.match(/^\s*function-call.*?:\s*(.*)$/i);
      const mToolR = line.match(/^\s*function-result.*?:\s*(.*)$/i);
      if (mUser) { flush(); role = 'user'; buf.push(mUser[1]); continue; }
      if (mAsst) { flush(); role = 'assistant'; buf.push(mAsst[1]); continue; }
      if (mToolC) { flush(); role = 'function-call'; buf.push(mToolC[1]); continue; }
      if (mToolR) { flush(); role = 'function-result'; buf.push(mToolR[1]); continue; }
      // continuation of current block
      buf.push(line);
    }
    flush();
    messages = tmp.filter(m => m.content);
  }

  // As a last resort, if still empty but we have an email artifact,
  // expose it as a single assistant message so the page shows something.
  if (messages.length === 0 && email_markdown) {
    messages = [{ role: 'assistant', content: email_markdown }];
  }

  return {
    json: {
      messages,                           // â† feed to your UI
      final_artifacts: { email_markdown },// â† show below "Transcript"
      raw: {
        had_memory: memMsgs.length > 0,
        used_vf_memory: messages.length && memMsgs.length === 0,
        stack_frames: stack.length
      }
    }
  };
});

return items;
